mapper.update:
UPDATE wk.wk_account  SET update_time =  #{updateTime}
 , name =  #{name}
 , user_name =  #{userName}
 , user_id =  #{userId}
 , bank_name =  #{bankName}
 , card_number =  #{cardNumber}
 , web_user_name =  #{webUserName}
 , web_password =  #{webPassword}
 , seg =  #{seg}
 , remark =  #{remark}
 , opt_id =  #{optId} 
WHERE id=#{id}

mapper.select:
SELECT id, name , user_name userName, bank_name bankName, card_number cardNumber, web_user_name webUserName, web_password webPassword, seg,remark, create_time createTime, update_time updateTime, opt_id optId FROM wk.wk_account

     AND  name =  #{name}
     AND  user_name =  #{userName}
     AND  bank_name =  #{bankName}
     AND  card_number =  #{cardNumber}
     AND  web_user_name =  #{webUserName}
     AND  web_password =  #{webPassword}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND
        (
        name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        user_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY seg DESC, update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_account

     AND  name =  #{name}
     AND  user_name =  #{userName}
     AND  bank_name =  #{bankName}
     AND  card_number =  #{cardNumber}
     AND  web_user_name =  #{webUserName}
     AND  web_password =  #{webPassword}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND
        (
        name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        user_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
