mapper.update:
UPDATE wk.wk_card SET update_time =  #{updateTime}
 , self =  #{self}
 , customer_id =  #{customerId}
 , card_name =  #{cardName}
 , name =  #{name}
 , id_number =  #{idNumber}
 , phone =  #{phone}
 , bank_name =  #{bankName}
 , card_number =  #{cardNumber}
 , password =  #{password}
 , bill_date =  #{billDate}
 , replay_date =  #{replayDate}
 , valid_date =  #{validDate}
 , total =  #{total}
 , bill =  #{bill}
 , replay_rate =  #{replayRate}
 , mini_fee =  #{miniFee}
 , cash_rate =  #{cashRate}
 , income =  #{income}
 , remark =  #{remark}
 , seg =  #{seg}
 , lock =  #{lock}
 , lockWorkerId =  #{lock_worker_id}
 , opt_id =  #{optId}  
WHERE id=#{id}

mapper.select:
SELECT
card.id,
card.self,
card.customer_id customerId,
customer.name customerName,
card.card_name cardName,
card.name,
card.id_number idNumber,
card.phone,
card.bank_name bankName,
card.card_number cardNumber,
card.password,
card.bill_date billDate,
card.replay_date replayDate,
card.valid_date validDate,
card.total,
card.bill,
card.replay_rate replayRate,
card.mini_fee miniFee,
card.cash_rate cashRate,
card.income,
card.remark,
card.seg,
card.lock,
card.lock_worker_id lockWorkerId,
card.create_time createTime,
card.update_time updateTime,
card.opt_id optId
FROM wk.wk_card card
LEFT JOIN wk.wk_customer customer  ON customer.id=card.customer_id

     AND  card.self =  #{self}
     AND  card.customer_id =  #{customerId}
     AND  card.card_name =  #{cardName}
     AND  card.name =  #{name}
     AND  card.id_number =  #{idNumber}
     AND  card.phone =  #{phone}
     AND  card.bank_name =  #{bankName}
     AND  card.card_number =  #{cardNumber}
     AND  card.password =  #{password}
     AND  card.bill_date =  #{billDate}
     AND  card.replay_date =  #{replayDate}
     AND  card.total =  #{total}
     AND  card.bill =  #{bill}
     AND  card.replay_rate =  #{replayRate}
     AND  card.mini_fee =  #{miniFee}
     AND  card.cash_rate =  #{cashRate}
     AND  card.income =  #{income}
     AND  card.remark =  #{remark}
     AND  card.seg =  #{seg}
     AND  card.opt_id =  #{optId} 
    AND card.create_time > #{startCreateTime}
    AND card.create_time < #{startCreateTime}
    AND card.update_time > #{startUpdateTime}
    AND card.update_time < #{endUpdateTime}

        AND
        (
        card.name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
        card.card_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
        card.phone LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
        card.id_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
        card.bank_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
        card.card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY card.seg DESC, card.update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_card

     AND  self =  #{self}
     AND  customer_id =  #{customerId}
     AND  card_name =  #{cardName}
     AND  name =  #{name}
     AND  id_number =  #{idNumber}
     AND  phone =  #{phone}
     AND  bank_name =  #{bankName}
     AND  card_number =  #{cardNumber}
     AND  password =  #{password}
     AND  bill_date =  #{billDate}
     AND  replay_date =  #{replayDate}
     AND  total =  #{total}
     AND  bill =  #{bill}
     AND  replay_rate =  #{replayRate}
     AND  mini_fee =  #{miniFee}
     AND  cash_rate =  #{cashRate}
     AND  income =  #{income}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId} 
    AND  create_time > #{startCreateTime}
    AND  create_time < #{startCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}

        AND
        (
        name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        card_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        phone LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        id_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        bank_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
