mapper.update:
UPDATE wk.wk_customer SET update_time =  #{updateTime}
 , name =  #{name}
 , id_number =  #{idNumber}
 , phone =  #{phone}
 , worker_id =  #{workerId}
 , font_img =  #{fontImg}
 , after_img =  #{afterImg}
 , home_img =  #{homeImg}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId }  
WHERE id=#{id}

mapper.select:
SELECT
customer.id,
customer.name,
customer.id_number idNumber,
customer.phone,
customer.worker_id workerId,
worker.name workerName,
customer.font_img fontImg,
customer.after_img afterImg,
customer.home_img homeImg,
customer.remark,
customer.seg,
customer.create_time createTime,
customer.update_time updateTime,
customer.opt_id optId
FROM wk.wk_customer customer
LEFT JOIN wk.wk_worker worker ON worker.id = customer.worker_id

     AND  customer.name =  #{name}
     AND  customer.id_number =  #{idNumber}
     AND  customer.phone =  #{phone}
     AND  customer.worker_id =  #{workerId}
     AND  customer.font_img =  #{fontImg}
     AND  customer.after_img =  #{afterImg}
     AND  customer.home_img =  #{homeImg}
     AND  customer.remark =  #{remark}
     AND  customer.seg =  #{seg}
     AND  customer.opt_id =  #{optId}
     AND  customer.create_time > #{startCreateTime}
     AND  customer.create_time < #{endCreateTime}
     AND  customer.update_time > #{startUpdateTime}
     AND  customer.update_time < #{endUpdateTime}

        AND
        (
        customer.name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
        customer.phone LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
            OR
        customer.id_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY customer.seg DESC, customer.update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_customer

     AND  name =  #{name}
     AND  id_number =  #{idNumber}
     AND  phone =  #{phone}
     AND  worker_id =  #{workerId}
     AND  font_img =  #{fontImg}
     AND  after_img =  #{afterImg}
     AND  home_img =  #{homeImg}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     AND  create_time > #{startCreateTime}
     AND  create_time < #{endCreateTime}
     AND  update_time > #{startUpdateTime}
     AND  update_time < #{endUpdateTime}

        AND
        (
        name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        phone LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        id_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
