mapper.update:
UPDATE wk.wk_model SET update_time =  #{updateTime}
 , name =  #{name}
 , remark =  #{remark}
 , icon =  #{icon}
 , seg =  #{seg}
 , opt_id =  #{optId} 
WHERE id=#{id}

mapper.select:
SELECT id, name, remark, seg, create_time createTime, update_time updateTime, opt_id optId FROM wk.wk_model

     AND  name =  #{name}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
    AND  create_time > #{startCreateTime}
    AND  create_time < #{endCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}
 ORDER BY seg DESC, update_time DESC   limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_model

     AND  name =  #{name}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
    AND  create_time > #{startCreateTime}
    AND  create_time < #{endCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}
