mapper.update:
UPDATE wk.wk_order SET update_time =  #{updateTime}
 , customer_id =  #{customerId}
 , card_id =  #{cardId}
 , type =  #{type}
 , total =  #{total}
 , rate =  #{rate}
 , fee =  #{fee}
 , discount =  #{discount}
 , real_fee =  #{realFee}
 , status =  #{status}
 , remark =  #{remark}

 , opt_id =  #{optId}  
WHERE id=#{id}

mapper.select:
SELECT
orders.id,
orders.customer_id customerId,
customer.name customerName,
orders.card_id cardId,
card.card_name cardName,
card.card_number cardNumber,
card.lock,
card.lock_worker_id lockWorkerId,
worker.name lockWorkerName,
card.bill_date billDate,
card.replay_date replayDate,
card.total total2,
orders.type,
orders.total,
orders.rate,
orders.fee,
orders.discount,
IFNULL(sumimport.sumBill,0) sumBill,
IFNULL(sumexport.sumBill,0) sumBill2,
orders.real_fee realFee,
orders.status,
orders.remark,
orders.seg,
orders.create_time createTime,
orders.update_time updateTime,
orders.opt_id optId
FROM wk.wk_order orders
LEFT JOIN wk.wk_customer customer ON customer.id = orders.customer_id
LEFT JOIN wk.wk_card card ON card.id = orders.card_id
LEFT JOIN wk.wk_worker worker ON worker.id = card.lock_worker_id
LEFT JOIN (
SELECT order_id id,sum(bill) sumBill  FROM wk.wk_order_import WHERE  result = 1 GROUP BY order_id
) sumimport ON sumimport.id = orders.id
LEFT JOIN (
SELECT order_id id,sum(import_bill) sumBill  FROM wk.wk_order_export  GROUP BY order_id
) sumexport ON sumexport.id = orders.id

     AND  orders.customer_id =  #{customerId}
     AND  orders.card_id =  #{cardId}
     AND  orders.type =  #{type}
     AND  orders.total =  #{total}
     AND  orders.rate =  #{rate}
     AND  orders.fee =  #{fee}
     AND  orders.discount =  #{discount}
     AND  orders.real_fee =  #{realFee}
     AND  orders.status =  #{status}
     AND  orders.remark =  #{remark}
     AND  orders.seg =  #{seg}
     AND  orders.opt_id =  #{optId}
    AND orders.create_time > #{startCreateTime}
    AND orders.create_time < #{endCreateTime}
    AND orders.update_time > #{startUpdateTime}
    AND orders.update_time < #{endUpdateTime}
  ORDER BY orders.seg DESC, orders.update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_order

     AND  customer_id =  #{customerId}
     AND  card_id =  #{cardId}
     AND  type =  #{type}
     AND  total =  #{total}
     AND  rate =  #{rate}
     AND  fee =  #{fee}
     AND  discount =  #{discount}
     AND  real_fee =  #{realFee}
     AND  status =  #{status}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
    AND  create_time > #{startCreateTime}
    AND  create_time < #{endCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}
