mapper.update:
UPDATE wk.wk_order_export SET update_time =  #{updateTime}, export_date = #{ exportDate}
, order_id = #{orderId} 
, type = #{type} 
, export_account_id = #{ exportAccountId} 
, import_account_id = #{ importAccountId} 
, bank_name = #{ bankName} 
, name = #{name} 
, card_number = #{ cardNumber} 
, import_bill = #{ importBill} 
, rate = #{rate} 
, fee = #{fee} 
, remark = #{remark} 
, seg = #{seg} 
, opt_id = #{ optId }
WHERE id=#{id}

mapper.select:
SELECT
export.id,
export.order_id orderId,
export.export_date exportDate,
export.type,
export.export_account_id exportAccountId,
account.name exportAccountName,
account.card_number exportAccountNumber,

card.card_name importAccountName,
 export.bank_name bankName,
export.name,
export.card_number cardNumber,
export.import_bill importBill,
export.rate,
export.fee,
export.remark,
export.seg,
export.create_time createTime,
export.update_time updateTime,
export.opt_id optId  FROM wk.wk_order_export export
LEFT JOIN wk.wk_account account ON account.id = export.export_account_id
LEFT JOIN wk.wk_card card ON card.id = export.import_account_id

    AND  export.order_id = #{orderId} 
    AND  export.type = #{type} 
    AND export. name = #{name} 
    AND  export.card_number = #{ cardNumber} 
    AND  export.import_bill = #{ importBill} 
    AND  export.rate = #{rate} 
    AND  export.fee = #{fee} 
    AND  export.remark = #{remark} 
    AND  export.seg = #{seg} 
    AND  export.opt_id = #{ optId }
    AND  create_time > #{startCreateTime}
    AND  create_time < #{endCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}

        AND
        (
        export.cardNumber LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        export.name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY  export.update_time ASC  limit #{pageStart},#{pageSize}

SELECT COUNT(*)   FROM wk.wk_order_export

    AND  order_id = #{orderId} 
    AND  type = #{type} 
    AND  name = #{name} 
    AND  card_number = #{ cardNumber} 
    AND  import_bill = #{ importBill} 
    AND  rate = #{rate} 
    AND  fee = #{fee} 
    AND  remark = #{remark} 
    AND  seg = #{seg} 
    AND  opt_id = #{ optId }
    AND  create_time > #{startCreateTime}
    AND  create_time < #{endCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}

        AND
        (
        cardNumber LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
