mapper.update:
UPDATE wk.wk_order_import SET update_time =  #{updateTime}, export_date = #{exportDate}
,id = #{id}
, order_id  = #{orderId}
, type = #{type}
, pos_id = #{posId}
, mall_name = #{mallName}
, consume_account_id = #{consumeAccountId}
, bill = #{bill}
, consume_type = #{consumeType}
, result = #{result}
, rate = #{rate}
, fee = #{fee}
, import_bill = #{importBill}
, should_bill = #{shouldBill}
, remark = #{remark}
, seg = #{seg}
, opt_id = #{optId}
WHERE id=#{id}

mapper.select:
SELECT
import.id id,
import.order_id  orderId,
import.export_date exportDate,
import.type type,
import.pos_id posId,
pos.name posName,
import.mall_name mallName,
import.consume_account_id consumeAccountId,
CONCAT(card.name,' ', card.card_number) consumeAccountName,
import.bill bill,
import.consume_type consumeType,
import.result result,
import.rate rate,
import.fee fee,
import.import_bill importBill,
import.should_bill shouldBill,
import.remark remark,
import.seg seg,
import.create_time createTime,
import.update_time updateTime,
import.opt_id optId
FROM wk.wk_order_import import
LEFT JOIN wk.wk_card card ON card.id = import.consume_account_id
LEFT JOIN wk.wk_pos pos ON pos.id = import.pos_id

    AND import.order_id  = #{orderId}
    AND import.export_date = #{exportDate}
    AND import.type = #{type}
    AND import.pos_id = #{posId}
    AND import.mall_name = #{mallName}
    AND import.consume_account_id = #{consumeAccountId}
    AND import.bill = #{bill}
    AND import.consume_type = #{consumeType}
    AND import.result = #{result}
    AND import.rate = #{rate}
    AND import.fee = #{fee}
    AND import.import_bill = #{importBill}
    AND import.should_bill = #{shouldBill}

    AND import.remark = #{remark} 
    AND import.seg = #{seg} 
    AND import.opt_id = #{ optId }
    AND  create_time > #{startCreateTime}
    AND  create_time < #{endCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}

        AND
        (
        import.mall_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        import.remark LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY  import.update_time ASC  limit #{pageStart},#{pageSize}

SELECT COUNT(*)   FROM wk.wk_order_import

    AND  order_id  = #{orderId}
    AND  export_date = #{exportDate}
    AND  type = #{type}
    AND  pos_id = #{posId}
    AND  mall_name = #{mallName}
    AND  consume_account_id = #{consumeAccountId}
    AND  bill = #{bill}
    AND  consume_type = #{consumeType}
    AND  result = #{result}
    AND  rate = #{rate}
    AND  fee = #{fee}
    AND  import_bill = #{importBill}
    AND  should_bill = #{shouldBill}

    AND  remark = #{remark} 
    AND  seg = #{seg} 
    AND  opt_id = #{ optId }
    AND  create_time > #{startCreateTime}
    AND  create_time < #{endCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}

        AND
        (
        mall_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        remark LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
