mapper.update:
UPDATE wk.wk_order_type SET update_time =  #{updateTime}
 , name =  #{name}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId} 
WHERE id=#{id}

mapper.select:
SELECT id, name, remark, seg, create_time, update_time, opt_id FROM wk.wk_order_type

     AND  name =  #{name}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
  ORDER BY seg ASC, update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_order_type

     AND  name =  #{name}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
