mapper.update:
UPDATE wk.wk_permission SET update_time =  #{updateTime}
 , model_id =  #{modelId}
 , name =  #{name}
 , action =  #{action}
 , opt_type =  #{optType}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId} 
WHERE id=#{id}

mapper.select:
SELECT ID, model_id, name, action, opt_type, remark, seg, create_time, update_time, opt_id FROM wk.wk_permission

     AND  model_id =  #{modelId}
     AND  name =  #{name}
     AND  action =  #{action}
     AND  opt_type =  #{optType}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
  limit #{pageStart},#{pageSize}
