mapper.update:
UPDATE wk.wk_permission SET update_time =  #{updateTime}
 , model_id =  #{modelId}
 , name =  #{name}
 , action =  #{action}
 , opt_type =  #{optType}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId} 
WHERE id=#{id}

mapper.select:
SELECT id, model_id modelId, name, action, opt_type optType, remark, seg, create_time createTime, update_time updateTime, opt_id optId FROM wk.wk_permission

     AND  model_id =  #{modelId}
     AND  name =  #{name}
     AND  action =  #{action}
     AND  opt_type =  #{optType}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
  ORDER BY seg DESC, update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_permission

     AND  model_id =  #{modelId}
     AND  name =  #{name}
     AND  action =  #{action}
     AND  opt_type =  #{optType}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
