mapper.update:
UPDATE wk.wk_pos SET update_time =  #{updateTime}
 , name =  #{name}
 , import_account_id =  #{importAccountId}
 , bank_name =  #{bankName}
 , card_number =  #{cardNumber}
 , rate1 =  #{rate1}
 , rate2 =  #{rate2}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId}  
WHERE id=#{id}

mapper.select:
SELECT
pos.id,
pos.NAME,
pos.import_account_id importAccountId,
account.name importAccountName,
pos.bank_name bankName,
pos.card_number cardNumber,
pos.rate1,
pos.rate2,
pos.remark,
pos.seg,
pos.create_time createTime,
pos.update_time updateTime,
pos.opt_id optId
FROM
wk.wk_pos pos
LEFT JOIN wk.wk_account account ON account.id = pos.import_account_id

     AND  pos.name =  #{name}
     AND  pos.bank_name =  #{bankName}
     AND  pos.card_number =  #{cardNumber}
     AND  pos.rate1 =  #{rate1}
     AND  pos.rate2 =  #{rate2}
     AND  pos.remark =  #{remark}
     AND  pos.seg =  #{seg}
     AND  pos.opt_id =  #{optId}
    AND  pos.create_time > #{startCreateTime}
    AND  pos.create_time < #{startCreateTime}
    AND  pos.update_time > #{startUpdateTime}
    AND pos.update_time < #{endUpdateTime}

        AND
        (
        pos.name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        pos.bank_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        pos.card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )

  ORDER BY pos.seg DESC, pos.update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_pos

     AND  name =  #{name}
     AND  bank_name =  #{bankName}
     AND  card_number =  #{cardNumber}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
    AND  create_time > #{startCreateTime}
    AND  create_time < #{startCreateTime}
    AND  update_time > #{startUpdateTime}
    AND  update_time < #{endUpdateTime}

        AND
        (
        name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        bank_name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        OR
        card_number LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
        )
