mapper.update:
UPDATE wk.wk_rate SET update_time =  #{updateTime}
 , rate =  #{rate}
 , remark =  #{remark}
 , seg =  #{seg}
 , opt_id =  #{optId} 
WHERE id=#{id}

mapper.select:
SELECT id, rate, remark, seg, create_time createTime, update_time updateTime, opt_id optId FROM wk.wk_rate

     AND  rate =  #{rate}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
  ORDER BY seg DESC, update_time DESC  limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk.wk_rate

     AND  rate =  #{rate}
     AND  remark =  #{remark}
     AND  seg =  #{seg}
     AND  opt_id =  #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}
