mapper.select:
SELECT id,name,remark,seg,create_time createTime, update_time updateTime, opt_id optId FROM wk_role

     AND name = #{name}
     AND remark = #{remark}
     AND seg = #{seg}
     AND opt_id = #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')


 ORDER BY seg DESC, update_time DESC   limit #{pageStart},#{pageSize}

SELECT COUNT(*) FROM wk_role

     AND name = #{name}
     AND remark = #{remark}
     AND seg = #{seg}
     AND opt_id = #{optId}
     create_time > #{startCreateTime}
     create_time < #{startCreateTime}
     update_time > #{startUpdateTime}
     update_time < #{endUpdateTime}

        AND name LIKE CONCAT(CONCAT('%', #{searchKey}),'%')
